type Icoon = keyof typeof SVGs;
type Icoontjes = [
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon,
  Icoon
];
const icoontjes = Object.keys(SVGs) as Icoon[];

function maakIcoon(icoon: Icoon, grootte: number | string = 16) {
  return SVGs[icoon].replace(/\$[wh]/g, String(grootte));
}
function reduceSVG(svg: string) {
  return svg
    .replaceAll("\n", "")
    .replace(/<svg[^>]*>/, "")
    .replace(/ id="[^"]*"/g, "");
}
function areSameSVG(svg1: string, svg2: string) {
  return reduceSVG(svg1) === reduceSVG(svg2);
}
const DEFAULTSVGs = [
  "vlag",
  "huis",
  "boekentas",
  "printer",
  "boek",
  "document",
  "erlenmeyer",
  "chat",
  "ster",
  "bliksem",
  "lamp",
] as const satisfies Icoontjes;

declare global {
  namespace SmartMonkey {
    interface MainPlugins {
      [id]: typeof plugin;
    }
    interface PluginsSettings {
      [id]: Icoontjes;
    }
  }
}

const id = "todo-icoontjes" as const;
const plugin = new PluginMain<typeof id>({
  id,
  version: "v0.1",
  inUseDefault: false,
  info: {
    name: "To-do: Andere icoontjes",
    description: "Geeft de mogelijkheid om de to-do icoontjes te veranderen.",
    author: "Kanti Kuijk",
  },
  activate: (settings = DEFAULTSVGs) => {
    const href =
      window.performance.getEntriesByType("navigation")[0]?.name ??
      window.location.href;
    if (!/^https:\/\/[a-zA-Z]+\.smartschool\.be\/planner\/?/.test(href)) return;

    function replaceSVG(svg: SVGElement) {
      const index = DEFAULTSVGs.findIndex((icoon) =>
        areSameSVG(svg.outerHTML, SVGs[icoon])
      );
      if (index === -1) return;
      const icoon = settings[index];
      if (icoon === DEFAULTSVGs[index] || !icoon) return;
      const size = svg.getAttribute("width") ?? "16";
      svg.outerHTML = maakIcoon(icoon, size);
    }

    const observer = new MutationObserver((mutationsList) => {
      for (const mutation of mutationsList) {
        if (mutation.type === "childList") {
          mutation.addedNodes.forEach((node) => {
            if (node instanceof SVGElement) {
              if (
                ["header__icon", "header", "detail-header", "todo"].every(
                  (cls) => node.closest(`.${cls}`)
                )
              ) {
                // Icoontje linksboven in dialog
                replaceSVG(node);
              } else if (
                [
                  "detail-todo-icon-and-color__icon",
                  "detail-todo-icon-and-color__icons",
                ].every((cls) => node.closest(`.${cls}`))
              ) {
                // Icoontjes in keuzemenu in dialog
                replaceSVG(node);
              } else if (
                [
                  "ple-content__icon",
                  "ple-content",
                  "ple",
                  "timegrid__content",
                ].every((cls) => node.closest(`.${cls}`))
              ) {
                // Icoontje in event van planner
                replaceSVG(node);
              } else if (
                [
                  "todo-column__icon-cell__wrapper__icon",
                  "todo-column__icon-cell",
                  "todo-column__row",
                  "todo-column__row",
                  "todo-column__todos",
                ].every((cls) => node.closest(`.${cls}`))
              ) {
                // Icoontje in de todo lijst zelf
                replaceSVG(node);
              }
            } else if (node instanceof HTMLElement) {
              node.querySelectorAll("svg").forEach((svg) => {
                if (
                  [
                    "ple-content__icon",
                    "ple-content",
                    "ple",
                    "timegrid__content",
                  ].every((cls) => svg.closest(`.${cls}`))
                ) {
                  // Icoontje in event van planner
                  replaceSVG(svg);
                } else if (
                  [
                    "todo-column__icon-cell__wrapper__icon",
                    "todo-column__icon-cell",
                    "todo-column__row",
                    "todo-column__row",
                    "todo-column__todos",
                  ].every((cls) => svg.closest(`.${cls}`))
                ) {
                  // Icoontje in de todo lijst zelf
                  replaceSVG(svg);
                }
              });
            }
          });
        }
      }
    });

    observer.observe(document.body, {
      childList: true, // Observe direct child additions/removals
      subtree: true, // Observe all descendants
    });
  },
  settingsDefault: DEFAULTSVGs,
  changeSettings: async function () {
    return new Promise((resolve) => {
      const settings: Icoontjes = [...this.user.settings];
      const saveSettingsBtn = emmet<"button">`
        button.smscButton.blue{Sluiten}
        `;
      saveSettingsBtn.addEventListener("click", () => {
        settingsDialog.close();
        resolve(settings);
      });
      const maakOpties = (i: number) => {
        const opties = icoontjes
          .map(
            (icoon) =>
              `option[value=${icoon}]{${icoon.replaceAll("_", " ")}}${
                icoon === settings[i] ? "[selected]" : ""
              }`
          )
          .join("+");

        const icoonPicker = emmet<"select">`
            select#smk-icoon-${String(i)}.smk-icoon-picker
              >${opties}
          `;
        const preview = emmet<"span">`span`;
        preview.innerHTML = maakIcoon(settings[i]!);
        icoonPicker.addEventListener("change", () => {
          settings[i] = icoonPicker.value as Icoon;
          preview.innerHTML = maakIcoon(settings[i]!);
        });
        const label = emmet<"label">`label{Icoon ${String(
          i + 1
        )}}[for=smk-icoon-${String(i)}]`;
        return emmet<"div">`div>${label}+${icoonPicker}+${preview}`;
      };
      const pickers = Array(11)
        .fill(undefined)
        .map((_, i) => maakOpties(i));
      const settingsDialog = emmet<"dialog">`
            dialog#smk-settings
              >h3{To-do: Andere icoontjes}
              +h4{Welke icoontjes wil je dat er gebruikt worden?}
              +${array2NodeList(pickers)}
              +em{Dit verandert enkel de icoontjes op deze computer.}
              +div.smscButtonContainer[style="margin-top:1em;"]
                >${saveSettingsBtn}
            `;
      document.body.append(settingsDialog);

      settingsDialog.showModal();
    });
  },
});

registerPlugin(plugin);
